import React from "react";
import {
  FlatList,
  StatusBar,
  StyleSheet,
  Text,
  TouchableOpacity, View
} from "react-native";
import { setRandomColor } from "../../helpers";
import Spinner from "../Spinner";

const HistoryBar = ({ navigation, hist }) => {
  const history = hist;

  console.log(history);

  if (!history) {
    return (
      <TouchableOpacity
        onPress={() => navigation.navigate("Start Workout")}
        style={[styles.item, { backgroundColor: setRandomColor() }]}
      >
        <Text>Create new workout</Text>
      </TouchableOpacity>
    );
  }

  const renderItem = ({ item }) => (
    <TouchableOpacity
      onPress={() =>
        navigation.navigate("Workout Details", {
          workout: item.workout,
          date: item.date,
        })
      }
      style={[styles.item, { backgroundColor: setRandomColor() }]}
    >
      <Text style={styles.title}>{item.workout.name}</Text>
    </TouchableOpacity>
  );

  return (
      <FlatList
        horizontal={true}
        data={history}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />

    
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    width: 160,
    maxHeight: 100,
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
    alignItems: "center",
  },
  title: {
    fontSize: 32,
  },
});

export default HistoryBar;
